// Analyze prompt context for Quark's business optimization
const prompt = $input.first().json.prompt || $input.first().json.body?.prompt || $input.first().json.message;
const timestamp = new Date().toISOString();

// Extract business context
const businessContext = {
  text: prompt,
  length: prompt.length,
  timestamp: timestamp,
  domain: detectBusinessDomain(prompt),
  complexity: detectComplexity(prompt),
  type: detectPromptType(prompt),
  profitability: detectProfitability(prompt),
  negotiation: detectNegotiation(prompt)
};

// Detect business domain from prompt
function detectBusinessDomain(prompt) {
  const domains = {
    'financial': ['budget', 'cost', 'price', 'revenue', 'profit', 'financial', 'money'],
    'negotiation': ['negotiate', 'deal', 'contract', 'agreement', 'bargain'],
    'strategy': ['strategy', 'plan', 'business', 'market', 'competition'],
    'operations': ['process', 'workflow', 'efficiency', 'optimization'],
    'sales': ['sales', 'marketing', 'customer', 'client', 'pitch']
  };

  const lowerPrompt = prompt.toLowerCase();
  for (const [domain, keywords] of Object.entries(domains)) {
    if (keywords.some(keyword => lowerPrompt.includes(keyword))) {
      return domain;
    }
  }
  return 'general-business';
}

// Detect complexity level
function detectComplexity(prompt) {
  const highComplexityKeywords = ['complex', 'advanced', 'sophisticated', 'comprehensive', 'multi-faceted'];
  const mediumComplexityKeywords = ['explain', 'describe', 'analyze', 'compare', 'evaluate'];

  const lowerPrompt = prompt.toLowerCase();
  if (highComplexityKeywords.some(keyword => lowerPrompt.includes(keyword))) {
    return 'high';
  } else if (mediumComplexityKeywords.some(keyword => lowerPrompt.includes(keyword))) {
    return 'medium';
  }
  return 'low';
}

// Detect prompt type
function detectPromptType(prompt) {
  const analyticalKeywords = ['analyze', 'evaluate', 'assess', 'review'];
  const creativeKeywords = ['create', 'design', 'develop', 'innovate'];
  const strategicKeywords = ['strategy', 'plan', 'optimize', 'improve'];
  const tacticalKeywords = ['implement', 'execute', 'action', 'do'];

  const lowerPrompt = prompt.toLowerCase();
  if (analyticalKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'analytical';
  if (creativeKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'creative';
  if (strategicKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'strategic';
  if (tacticalKeywords.some(keyword => lowerPrompt.includes(keyword))) return 'tactical';
  return 'analytical';
}

// Detect profitability focus
function detectProfitability(prompt) {
  const profitabilityKeywords = ['profit', 'revenue', 'cost', 'budget', 'ROI', 'return', 'money'];
  const lowerPrompt = prompt.toLowerCase();
  return profitabilityKeywords.some(keyword => lowerPrompt.includes(keyword));
}

// Detect negotiation elements
function detectNegotiation(prompt) {
  const negotiationKeywords = ['negotiate', 'deal', 'contract', 'agreement', 'bargain', 'terms'];
  const lowerPrompt = prompt.toLowerCase();
  return negotiationKeywords.some(keyword => lowerPrompt.includes(keyword));
}

return {
  businessContext,
  originalPrompt: prompt,
  processingId: `quark_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  crewMember: 'Quark'
};

// Optimize LLM selection for Quark based on business context
const businessContext = $input.first().json.businessContext;
const crewMember = 'Quark';

// Quark's business expertise mapping
const quarkExpertise = {
  'financial': ['cost-optimization', 'budget-analysis', 'revenue-maximization'],
  'negotiation': ['deal-making', 'contract-optimization', 'bargaining'],
  'strategy': ['business-strategy', 'market-analysis', 'competitive-advantage'],
  'operations': ['process-optimization', 'efficiency-improvement', 'resource-allocation'],
  'sales': ['sales-strategy', 'customer-relationship', 'pitch-optimization']
};

// LLM optimization based on business context and Quark's expertise
function selectOptimalLLMForQuark(context) {
  const expertise = quarkExpertise[context.domain] || ['general-business'];

  // Context-based LLM selection for business optimization
  if (context.type === 'analytical' && context.profitability) {
    return 'openai/gpt-4o'; // Best for financial analysis and ROI calculations
  }
  if (context.type === 'strategic' && context.domain === 'strategy') {
    return 'anthropic/claude-3-opus'; // Best for complex business strategy
  }
  if (context.negotiation || context.domain === 'negotiation') {
    return 'openai/gpt-4-turbo'; // Best for negotiation tactics and deal-making
  }
  if (context.type === 'creative' && context.domain === 'sales') {
    return 'anthropic/claude-3-sonnet'; // Best for creative sales approaches
  }
  if (context.complexity === 'high') {
    return 'anthropic/claude-3-opus'; // Best for complex business problems
  }
  if (context.complexity === 'low') {
    return 'anthropic/claude-3-haiku'; // Cost-effective for simple queries
  }

  // Default: GPT-4o for general business optimization
  return 'openai/gpt-4o';
}

const optimalLLM = selectOptimalLLMForQuark(businessContext);
const confidence = Math.random() * 0.3 + 0.7; // Simulate confidence between 0.7-1.0

// Generate Quark-specific reasoning
const reasoning = `Selected ${optimalLLM} for Quark based on ${businessContext.type} ${businessContext.domain} context. ` +
  `Quark's expertise: ${quarkExpertise[businessContext.domain]?.join(', ') || 'general-business'}. ` +
  `Profitability focus: ${businessContext.profitability ? 'Yes' : 'No'}, ` +
  `Negotiation elements: ${businessContext.negotiation ? 'Yes' : 'No'}, ` +
  `Complexity: ${businessContext.complexity}`;

return {
  crewMember,
  optimalLLM,
  confidence,
  reasoning,
  businessContext,
  originalPrompt: $input.first().json.originalPrompt,
  processingId: $input.first().json.processingId,
  timestamp: new Date().toISOString()
};

