const timestamp = new Date().toISOString();

// Simulate system metrics collection
const systemMetrics = {
  timestamp,
  totalPrompts: Math.floor(Math.random() * 100) + 50,
  hallucinationsDetected: Math.floor(Math.random() * 10) + 1,
  correctionsApplied: Math.floor(Math.random() * 8) + 1,
  averageProcessingTime: Math.floor(Math.random() * 5000) + 2000,
  systemHealth: Math.random() * 0.4 + 0.6 // 60-100%
};

// Simulate crew member accuracy data
const crewMemberAccuracy = {
  'Captain Picard': Math.random() * 0.2 + 0.8,
  'Commander Data': Math.random() * 0.15 + 0.85,
  'Counselor Troi': Math.random() * 0.25 + 0.75,
  'Lieutenant Worf': Math.random() * 0.2 + 0.8,
