};

// Simulate LLM performance data
const llmPerformance = {
  'anthropic/claude-3-opus': Math.random() * 0.15 + 0.85,
  'anthropic/claude-3-sonnet': Math.random() * 0.18 + 0.82,
  'openai/gpt-4-turbo': Math.random() * 0.16 + 0.84,
  'openai/gpt-4': Math.random() * 0.17 + 0.83,
  'openai/gpt-3.5-turbo': Math.random() * 0.2 + 0.8
};

// Generate trend analysis
const trends = {
  hallucinationRate: {
    current: systemMetrics.hallucinationsDetected / systemMetrics.totalPrompts,
    trend: Math.random() > 0.5 ? 'decreasing' : 'increasing',
    change: Math.random() * 0.1 - 0.05 // -5% to +5%
  },
  systemHealth: {
    current: systemMetrics.systemHealth,
    trend: systemMetrics.systemHealth > 0.8 ? 'stable' : 'declining',
    change: Math.random() * 0.05 - 0.025 // -2.5% to +2.5%
  },
  processingTime: {
    current: systemMetrics.averageProcessingTime,
    trend: systemMetrics.averageProcessingTime < 3000 ? 'improving' : 'degrading',
    change: Math.random() * 500 - 250 // -250ms to +250ms
  }
};

// Generate alerts
const alerts = [];
if (systemMetrics.systemHealth < 0.7) {
  alerts.push({
    type: 'warning',
    message: 'System health below 70%',
    severity: 'medium'
  });
}
if (systemMetrics.hallucinationsDetected / systemMetrics.totalPrompts > 0.15) {
  alerts.push({
    type: 'error',
    message: 'Hallucination rate above 15%',
    severity: 'high'
  });
}
if (systemMetrics.averageProcessingTime > 10000) {
  alerts.push({
    type: 'warning',
    message: 'Processing time above 10 seconds',
    severity: 'medium'
  });
}

return {
  systemMetrics,
  crewMemberAccuracy,
  llmPerformance,
  trends,
  alerts,
  timestamp,
  monitoringPeriod: '5 minutes'
};

// Process alerts and generate notifications
const monitoringData = $input.first().json;
const alerts = monitoringData.alerts;

// Generate alert notifications
const notifications = alerts.map(alert => ({
  id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  type: alert.type,
  severity: alert.severity,
  message: alert.message,
  timestamp: monitoringData.timestamp,
  systemHealth: monitoringData.systemMetrics.systemHealth,
  recommendedActions: generateRecommendedActions(alert)
}));

function generateRecommendedActions(alert) {
  switch (alert.type) {
    case 'warning':
      return [
        'Review system configuration',
        'Check crew member performance',
        'Monitor for additional issues'
      ];
    case 'error':
      return [
        'Immediate investigation required',
        'Review hallucination detection thresholds',
        'Consider system restart if persistent',
        'Escalate to system administrator'
      ];
    default:
      return ['Monitor system closely'];
  }
}

return {
  ...monitoringData,
  notifications,
  alertCount: alerts.length
};

// Generate monitoring dashboard data
const monitoringData = $input.first().json;

// Format dashboard data
const dashboardData = {
  timestamp: monitoringData.timestamp,
  summary: {
    systemHealth: Math.round(monitoringData.systemMetrics.systemHealth * 100),
    totalPrompts: monitoringData.systemMetrics.totalPrompts,
    hallucinationRate: Math.round((monitoringData.systemMetrics.hallucinationsDetected / monitoringData.systemMetrics.totalPrompts) * 100),
    correctionsApplied: monitoringData.systemMetrics.correctionsApplied,
    averageProcessingTime: monitoringData.systemMetrics.averageProcessingTime
  },
  trends: monitoringData.trends,
  alerts: monitoringData.alerts || [],
  notifications: monitoringData.notifications || [],
  crewPerformance: Object.entries(monitoringData.crewMemberAccuracy).map(([member, accuracy]) => ({
    crewMember: member,
    accuracy: Math.round(accuracy * 100),
    status: accuracy > 0.8 ? 'excellent' : accuracy > 0.7 ? 'good' : 'needs_attention'
  })),
  llmPerformance: Object.entries(monitoringData.llmPerformance).map(([llm, performance]) => ({
    llm,
    performance: Math.round(performance * 100),
    status: performance > 0.85 ? 'optimal' : performance > 0.8 ? 'good' : 'needs_optimization'
  })),
  recommendations: generateRecommendations(monitoringData)
};

function generateRecommendations(data) {
  const recommendations = [];

  if (data.systemMetrics.systemHealth < 0.8) {
    recommendations.push({
      type: 'system_health',
      priority: 'high',
      message: 'System health is below optimal. Consider reviewing hallucination detection thresholds.'
    });
  }

  if (data.systemMetrics.averageProcessingTime > 5000) {
    recommendations.push({
      type: 'performance',
      priority: 'medium',
      message: 'Processing time is high. Consider optimizing LLM selection or reducing crew size.'
    });
  }

  const lowAccuracyCrew = Object.entries(data.crewMemberAccuracy)
    .filter(([_, accuracy]) => accuracy < 0.8)
    .map(([member, _]) => member);

  if (lowAccuracyCrew.length > 0) {
    recommendations.push({
      type: 'crew_training',
      priority: 'medium',
      message: `Crew members with low accuracy: ${lowAccuracyCrew.join(', ')}. Consider additional training.`
    });
  }

  return recommendations;
}

return dashboardData;

