// Process and format job opportunities data
const supabaseData = $input.all()[0].json;

// Check if we got data from Supabase
if (!supabaseData || supabaseData.length === 0) {
  // Return empty data with proper structure
  return {
    data: [],
    total: 0,
    message: "No job opportunities found",
    timestamp: new Date().toISOString(),
    source: "supabase"
  };
}

// Format the data for the frontend
const formattedJobs = supabaseData.map(job => ({
  id: job.id,
  company: job.company || 'Unknown Company',
  position: job.position || 'Unknown Position',
  location: job.location || 'Unknown Location',
  remote_option: job.remote_option || 'Not specified',
  salary_range: job.salary_range || 'Not specified',
  description: job.description || 'No description available',
  requirements: job.requirements || 'No requirements specified',
  benefits: job.benefits || 'No benefits specified',
  application_url: job.application_url || '#',
  source: job.source || 'supabase',
  scraped_at: job.scraped_at || new Date().toISOString(),
  alex_ai_score: job.alex_ai_score || 0,
  st_louis_area: job.st_louis_area || false,
  st_louis_focus: job.st_louis_focus || false,
  created_at: job.created_at || new Date().toISOString(),
  updated_at: job.updated_at || new Date().toISOString()
}));

return {
  data: formattedJobs,
  total: formattedJobs.length,
  message: `Found ${formattedJobs.length} job opportunities`,
  timestamp: new Date().toISOString(),
  source: "supabase"
};
